import React, {useState} from 'react';
import { timer, concat, NEVER, of, fromEvent } from 'rxjs';
import { scan, tap, filter, switchMap } from 'rxjs/operators';
import { emitKeypressEvents } from 'readline'
import DisplayComponent from './Components/display/DisplayComponent';
import BtnComponent from './Components/btn/BtnComponent';
import './App.css';

function App() {
  const [time, setTime] = useState({ s:0, m:0, h:0});
  const [interv, setInterv] = useState();

  process.stdin.setRawMode(true);
  emitKeypressEvents(process.stdin);

  const keypresses$ = fromEvent(process.stdin, 'keypress', (_, key) => key);
  const pauser$ = keypresses$.pipe(
    tap(key => {
      if (key && key.ctrl && key.name == 'c') {
        process.exit(0);
      }
    }),
    filter(key => key.name === 'p'),
    scan(acc => !acc, false),
  );

  const starter$ = of(false);
  concat(starter$, pauser$)
    .pipe(
      switchMap(stopped => (stopped ? NEVER : timer(0, 1000))),
      scan(acc => acc + 1, 0),
    )
    .subscribe(console.log);

  const stop = () => {
    clearInterval(interv);
    setTime({ s:0, m:0, h:0})

  };

  const wait = () => {
    clearInterval(interv);


  };

  const reset = () =>{
    clearInterval(interv);
    setTime({ s:0, m:0, h:0});
  }

  return (
    <div className="main-section">
     <div className="clock-holder">
          <div className="stopwatch">
               <DisplayComponent time={time}/>
               <BtnComponent   reset={reset} stop={stop} start={start} wait={wait}/>
          </div>
     </div>
    </div>
  );
}

export default App;
